import React, { PureComponent } from "react";
import WeekCalendar from "react-week-calendar/src/WeekCalendar";
import moment from "moment";
import styled from "styled-components";
import "react-week-calendar/src/style.css";

import RangeDisplay from "./RangeDisplay";
import EventComponent from "./EventDisplay";
import ScheduleEventForm from "./ScheduleEventForm";

const Title = styled.h2`
  color: #273746;
  display: inline-block;
  width: 60%;
  padding-left: 15px;
`;
const HeaderContainer = styled.div`
  display: flex;
`;
const WeekCalendarConatiner = styled.div`
  padding: 10px 10px 0 10px;
  height: 85vh;
`;

const selectedEvents = [
];

class WeekCalendarDep extends PureComponent {
  state = {
    startDate: moment().weekday(0),
    endDate: moment().weekday(6),
    firstDay: moment().weekday(0),
    selectedIntervals: selectedEvents,
    isFormOpen: false,
  };
  startTime = moment().hour(0);
  endTime = moment().hour(23);

  goToNextWeek = () => {
    this.setState((state) => {
      return {
        firstDay: this.state.firstDay.clone().add(7, "days"),
        startDate: this.state.startDate.clone().add(7, "days"),
        endDate: this.state.endDate.clone().add(7, "days"),
      };
    });
  };

  goToPrevWeek = () => {
    this.setState((state) => {
      return {
        firstDay: this.state.firstDay.clone().subtract(7, "days"),
        startDate: this.state.startDate.clone().subtract(7, "days"),
        endDate: this.state.endDate.clone().subtract(7, "days"),
      };
    });
  };

  onEventClick = (e) => {
    alert(e);
  };

  formHandler = () => {
    this.setState({ isFormOpen: true });
  };

  handleFormClose = (values) => {
    // alert(JSON.stringify(moment()).split("T")[0]);
    const differenceInTime =
      new Date(moment()).getTime() - new Date(values.start).getTime();
    const differenceInDays = Math.round(differenceInTime / (1000 * 3600 * 24));
    const eventData = {
      startHour: new Date(values.start).getHours(),
      startMinute: new Date(values.start).getMinutes(),
      endHour: new Date(values.end).getHours(),
      endMinute: new Date(values.end).getMinutes(),
    };

    if (differenceInDays) {
      const newEvent = {
        start: moment()
          .subtract(differenceInDays, "days")
          .hour(eventData.startHour)
          .minute(eventData.startMinute),
        end: moment()
          .subtract(differenceInDays, "days")
          .hour(eventData.endHour)
          .minute(eventData.endMinute),
      };
      selectedEvents.push(newEvent);
    } else {
      const newEvent = {
        start: moment()
          .add(differenceInDays, "days")
          .hour(eventData.startHour)
          .minute(eventData.startMinute),
        end: moment()
          .add(differenceInDays, "days")
          .hour(eventData.endHour)
          .minute(eventData.endMinute),
      };
      selectedEvents.push(newEvent);
    }
    this.setState({ isFormOpen: false });
  };

  render() {
    return (
      <>
        <HeaderContainer>
          <Title>Timeline</Title>
          <RangeDisplay
            startDate={this.state.startDate.format("DD  - ").toString()}
            endDate={this.state.endDate.format("DD MMMM, yy").toString()}
            prevWeek={this.goToPrevWeek}
            nextWeek={this.goToNextWeek}
          />
        </HeaderContainer>
        <WeekCalendarConatiner>
          <WeekCalendar
            firstDay={this.state.firstDay}
            startTime={this.startTime}
            endTime={this.endTime}
            scaleUnit={60}
            eventSpacing={0}
            selectedIntervals={this.state.selectedIntervals}
            onEventClick={this.onEventClick}
            eventComponent={EventComponent}
            useModal={false}
            cellHeight={100}
            formHandler={this.formHandler}
          />
        </WeekCalendarConatiner>
        {this.state.isFormOpen && (
          <ScheduleEventForm
            isFormOpen={this.state.isFormOpen}
            handleFormClose={this.handleFormClose}
          />
        )}
      </>
    );
  }
}

export default WeekCalendarDep;
