import React, {useState } from "react";
import PropTypes from "prop-types";
import WeekCalendar from "react-week-calendar";
import moment from "moment";
import "react-week-calendar/dist/style.css";

import { selectedEvents } from "..";

const WeekSchedular = () => {
  const [state,setState]= useState({
    startDate: moment().weekday(0),
    endDate: moment().weekday(6),
    firstDay: moment().weekday(0),
    selectedIntervals: selectedEvents
  });
  const startTime = moment().hour(8);
  const endTime = moment().hour(18);
  const goToNextWeek = () => {
    setState(
     {
        firstDay: state.firstDay.clone().add(7, "days"),
        startDate: state.startDate.clone().add(7, "days"),
        endDate:state.endDate.clone().add(7, "days")
    
    });
  };
  const goToPrevWeek = () => {
    setState(
    {
        firstDay:state.firstDay.clone().subtract(7, "days"),
        startDate: state.startDate.clone().subtract(7, "days"),
        endDate:state.endDate.clone().subtract(7, "days")
     
    });
  };
  const onEventClick = (e) => {
    alert(e);
  };

    return (
      <div className="App">
        <span>{state.startDate.toString()}</span>
        <span>{state.endDate.toString()}</span>
        <button onClick={goToPrevWeek}>Prev</button>
        <button onClick={goToNextWeek}>Next</button>
        <WeekCalendar
          firstDay={state.firstDay}
          startTime={startTime}
          endTime={endTime}
          scaleUnit={60}
          eventSpacing={0}
          selectedIntervals={state.selectedIntervals}
          onEventClick={onEventClick}
          eventComponent={EventComponent}
          useModal={false}
        />
      </div>
    );

}

const EventComponent = (props) => (
  <div
    style={{
      backgroundColor: "white",
      height: "100%",
      border: "1px solid blue"
    }}
  >
    {/* {JSON.stringify(props)} */}
    Event Name
  </div>
);

EventComponent.propTypes = {
  start: PropTypes.instanceOf(moment).isRequired
};

export default WeekSchedular;
