import React from "react";
import { Formik, Field, Form } from "formik";
import styled from "styled-components";

const SideBar = styled.div`
  position: absolute;
  z-index: 15;
  top: 0;
  right: 0;
  background-color: #ecf0f1;
  width: ${(props) => (props.isFormOpen ? "30%" : "0%")};
  height: 85vh;
  margin-top: 11.4vh;
  margin-botton: 5vh;
  transition: width 2s ease;
`;

const Label = styled.div`
  padding: 15px 0 15px 0;
  font-family:arial;
  font-size:12px;
  color: #808b96;
  font-weight:bold;
`;
const FormikField = styled(Field)`
  padding: 15px 5px;
  outline: none;
  border: none;
  width: 100%;
  font-family:arial;
  font-size:12px;
  color: #808b96;
  font-weight:bold;
`;

const FormTitle = styled.h3`
  text-align: center;
  font-family: arial;
  color: #808b96;
  padding-top: 15px;
`;

const FormContainer = styled.div`
  margin: auto;
  width: 60%;
  padding-top: 15%;
`;

const ScheduleEventForm = (props) => {
  return (
    <>
      <SideBar isFormOpen={props.isFormOpen}>
        <FormTitle>Schedule My Event</FormTitle>
        <FormContainer>
          <Formik
            initialValues={{ email: "", password: "" }}
            onSubmit={(values, { setSubmitting }) => {
              setTimeout(() => {
                alert(JSON.stringify(values, null, 2));
                setSubmitting(false);
              }, 400);
            }}
          >
            <Form>
              <Label>Title</Label>
              <FormikField type="text" />
              <Label>Event Start</Label>
              <FormikField type="datetime-local" />
              <Label>Event End</Label>
              <FormikField type="datetime-local" />
            </Form>
          </Formik>
        </FormContainer>
      </SideBar>
    </>
  );
};

export default ScheduleEventForm;
