import React, { useState } from "react";
import PropTypes from "prop-types";
import WeekCalendar from "react-week-calendar";
import moment from "moment";
import "react-week-calendar/dist/style.css";

const WeekSchedular = () => {
  const scheduledEvents = [
    {
      start: moment()
        .add(1, "days")
        .hour(9)
        .minute(0),
      end: moment()
        .add(1, "days")
        .hour(10)
        .minute(0),
    },
    {
      start: moment()
        .add(1, "days")
        .hour(10)
        .minute(0),
      end: moment()
        .add(1, "days")
        .hour(12)
        .minute(0),
    },
    {
      start: moment()
        .add(2, "days")
        .hour(10)
        .minute(0),
      end: moment()
        .add(2, "days")
        .hour(11)
        .minute(0),
    },
  ];
  const [weekDetails, setWeekDetails] = useState({
    weekStartDate: moment().weekday(0),
    weekEndDate: moment().weekday(6),
    firstDayOfWeek: moment().weekday(0),
    selectedIntervals: scheduledEvents,
  });
  const startTime = moment().hour(9);
  const endTime = moment().hour(18);

  const goToNextWeek = () => {
    setWeekDetails({
      firstDayOfWeek: weekDetails.firstDayOfWeek.clone().add(7, "days"),
      weekStartDate: weekDetails.weekStartDate.clone().add(7, "days"),
      weekEndDate: weekDetails.weekEndDate.clone().add(7, "days"),
    });
  };

  const goToPrevWeek = () => {
    setWeekDetails({
      firstDayOfWeek: weekDetails.firstDayOfWeek.clone().subtract(7, "days"),
      weekStartDate: weekDetails.weekStartDate.clone().subtract(7, "days"),
      weekEndDate: weekDetails.weekEndDate.clone().subtract(7, "days"),
    });
  };

  const onEventClick = (e) => {
    alert(e);
  };

  return (
    <div>
      <span>{weekDetails.weekStartDate.toString()}</span>
      <span>{weekDetails.weekEndDate.toString()}</span>
      <button onClick={goToPrevWeek}>Prev</button>
      <button onClick={goToNextWeek}>Next</button>
      <WeekCalendar
        firstDay={weekDetails.firstDayOfWeek}
        startTime={startTime}
        endTime={endTime}
        scaleUnit={60}
        eventSpacing={0}
        selectedIntervals={weekDetails.selectedIntervals}
        onEventClick={onEventClick}
        eventComponent={EventComponent}
        useModal={false}
      />
    </div>
  );
};

const EventComponent = (props) => (
  <div
    style={{
      backgroundColor: "white",
      height: "100%",
      border: "1px solid blue",
    }}
  >
    {/* {JSON.stringify(props)} */}
    Event Name
  </div>
);

EventComponent.propTypes = {
  start: PropTypes.instanceOf(moment).isRequired,
};

export default WeekSchedular;
