import React from "react";
import moment from "moment";
import PropTypes from "prop-types";
import styled from "styled-components";

const Event = styled.div`
  border-radius: 3px;
  background-color: ${(props) =>
    props.eventColor ? props.eventColor : "#F5B7B1"};
  height: 100%;
  border: 0.1px solid #fbfcfc;
`;

const EventDuration = styled.div`
  padding: 10% 10% 10% 4%;
  overflow: hidden;
  font-family: arial;
  font-size: 12px;
  font-weight: bold;
`;
const EventTitle = styled.span``;

const color={
  colorOne:"#F1948A",
  colorTwo:"#1ABC9C",
  colorThree:"#5DADE2",
  colorFour:"#884EA0",
  colorFive:"#F1C40F",
  colorSix:"#34495E",
  colorSeven:"#C0392B",
  colorEight:"#884EA0",
}

const EventComponent = (props) => {
  return (
    <>
      <Event eventColor={"#F5B7B1"}>
        <EventDuration>
          <EventTitle>{props.eventTitle}</EventTitle>
          <br />
          <br />
          {props.start.format("hh:mm - ").toString()}
          {props.end.format("hh:mm").toString()}
        </EventDuration>
      </Event>
    </>
  );
};

EventComponent.propTypes = {
  start: PropTypes.instanceOf(moment).isRequired,
  end: PropTypes.instanceOf(moment).isRequired,
};

export default EventComponent;
