import React,{useEffect, useState} from "react";
import { Formik, Field, Form } from "formik";
import styled from "styled-components";

const SideBar = styled.div`
  position: absolute;
  z-index: 15;
  top: 0;
  right: 0;
  background-color: #ecf0f1;
  width: ${(props) => (props.isFormOpen ? "30%" : "0%")};
  height: 85vh;
  margin-top: 11.4vh;
  margin-botton: 5vh;
  transition: width 2s ease;
`;

const Label = styled.div`
  padding: 20px 0 10px 0;
  font-family: arial;
  font-size: 12px;
  color: #808b96;
  font-weight: bold;
`;
const FormikField = styled(Field)`
  padding: 15px 5px;
  outline: none;
  border: none;
  width: 100%;
  font-family: arial;
  font-size: 12px;
  color: #808b96;
  font-weight: bold;
`;

const FormTitle = styled.h3`
  text-align: center;
  font-family: arial;
  color: #808b96;
  padding-top: 15px;
`;

const FormContainer = styled.div`
  margin: auto;
  width: 60%;
  padding-top: 15%;
`;

const Button = styled.button`
  margin: 10% 0 0 40%;
  padding: 10px 20px;
  background-color: #1abc9c;
  border: none;
  font-family: arial;
  color: #fff;
  &:hover {
    background-color: #17a589;
    cursor: pointer;
  }
`;

const FormikFieldTime = styled(Field)`
padding: 15px 5px;
  outline: none;
  border: none;
  width: 50%;
  font-family: arial;
  font-size: 12px;
  color: #808b96;
  font-weight: bold;
`

const ScheduleEventForm = (props) => {
  return (
    <>
      <SideBar isFormOpen={props.isFormOpen}>
        <FormTitle>Schedule My Event</FormTitle>
        <FormContainer>
          <Formik
            initialValues={{ title: "", start: "" ,end:""}}
            onSubmit={(values, { setSubmitting }) => {
              setTimeout(() => {
                setSubmitting(false);
                props.handleFormClose(values);
              }, 400);
            }}
          >
            <Form>
              <Label>Title</Label>
              <FormikField type="text" name="title" />
              <Label>Event Start</Label>
              <div style={{display:"flex"}}>
              <FormikFieldTime type="text" name="start"/>
              <FormikFieldTime type="text" name="start"/>
              </div>
              <Label>Event End</Label>
              <FormikField type="datetime-local" name="end"  />
              <Button>Create</Button>
            </Form>
          </Formik>
        </FormContainer>
      </SideBar>
    </>
  );
};

export default ScheduleEventForm;
