import React from "react";
import styled from "styled-components";

const DropdownContainer = styled.div`
padding-top:12px
`
const DropdownSelect = styled.select`
height:35px;
padding:0 12px;
font-weight:bold;
color:#85929E;
border:2px solid #E5E7E9;
border-radius:5px;
`;

const Dropdown = () => {
  return (
    <>
    <DropdownContainer>
      <DropdownSelect>
        <option>&#128197; Week</option>
        <option disabled>&#128197; Month</option>
      </DropdownSelect>
      </DropdownContainer>
    </>
  );
};

export default Dropdown;
