import React from "react";
import { Formik, Field, Form } from "formik";
import styled from "styled-components";

const SideBar = styled.div`
  position: absolute;
  z-index: 15;
  top: 0;
  right: 0;
  background-color: #ecf0f1;
  width: ${(props) => (props.isFormOpen ? "30%" : "0%")};
  height: 85vh;
  margin-top: 11.4vh;
  margin-botton: 5vh;
  transition: width 2s ease;
`;

const Label = styled.div`
  padding: 10px 0 10px 0;
`;
const FormikField = styled(Field)`
  padding: 5px 5px;
  outline: none;
  border: none;
`;

const FormTitle = styled.h3`
  text-align: center;
  font-family:arial;
  color:#ABB2B9 ;
`;

const ScheduleEventForm = (props) => {
  return (
    <>
      <SideBar isFormOpen={props.isFormOpen}>
        <FormTitle>Schedule Event</FormTitle>

        <Formik
          initialValues={{ email: "", password: "" }}
          onSubmit={(values, { setSubmitting }) => {
            setTimeout(() => {
              alert(JSON.stringify(values, null, 2));
              setSubmitting(false);
            }, 400);
          }}
        >
          <Form>
            <Label>Title</Label>
            <FormikField type="text" />
            <Label>Event Start</Label>
            <Field type="datetime-local"></Field>
            <Label>Event End</Label>
            <Field type="datetime-local"></Field>
          </Form>
        </Formik>
      </SideBar>
    </>
  );
};

export default ScheduleEventForm;
