import React from "react";
import PropTypes from "prop-types";

const propTypes = {
  start: PropTypes.object.isRequired,
  end: PropTypes.object.isRequired,
  value: PropTypes.string,
  onRemove: PropTypes.func.isRequired,
  onSave: PropTypes.func.isRequired,
  actionType: PropTypes.string // eslint-disable-line react/no-unused-prop-types
};

const defaultProps = {
  value: ""
};

class ModalCalendar extends React.Component {
  handleRemove = () => {
    this.props.onRemove();
  };

  handleSave = () => {
    const { value } = this.input;
    this.props.onSave({
      value
    });
  };

  renderText() {
    const { start, end } = this.props;

    if (start.isSame(end, "day")) {
      return (
        <span>{`${start.format("Do MMM., HH:mm")} - ${end.format(
          "HH:mm"
        )}`}</span>
      );
    }
    return (
      <span>{`${start.format("Do MMM.")} - ${end.format(
        "Do MMM."
      )}, ${start.format("HH:mm")} - ${end.format("HH:mm")}`}</span>
    );
  }

  render() {
    const { value } = this.props;
    console.log("USAHDAS: ", this.props);
    return (
      <div className="customModal">
        <div className="customModal__text">{this.renderText()}</div>
        <input
          ref={(el) => {
            this.input = el;
          }}
          className="customModal__input"
          type="text"
          placeholder="Enter something"
          defaultValue={value}
        />
        <button className="customModal__button" onClick={this.handleRemove}>
          Delete
        </button>
        <button
          className="customModal__button customModal__button_float_right"
          onClick={this.handleSave}
        >
          Save
        </button>
      </div>
    );
  }
}

ModalCalendar.propTypes = propTypes;
ModalCalendar.defaultProps = defaultProps;
export default ModalCalendar;
