import React, { PureComponent } from "react";
import PropTypes from "prop-types";
import WeekCalendar from "react-week-calendar";
import moment from "moment";
import styled from "styled-components";

import "react-week-calendar/dist/style.css";

const WeekCalendarContainer = styled(WeekCalendar)`
> div:nth-child(3) {
  background-color:red;
}
`

const selectedEvents = [
  {
    start: moment().add(1, "days").hour(9).minute(0),
    end: moment().add(1, "days").hour(10).minute(0)
  },
  {
    start: moment().add(1, "days").hour(10).minute(0),
    end: moment().add(1, "days").hour(12).minute(0)
  },
  {
    start: moment().add(2, "days").hour(10).minute(0),
    end: moment().add(2, "days").hour(11).minute(0)
  }
];

class WeekCalendarDep extends PureComponent {
  state = {
    startDate: moment().weekday(0),
    endDate: moment().weekday(6),
    firstDay: moment().weekday(0),
    selectedIntervals: selectedEvents
  };
  startTime = moment().hour(8);
  endTime = moment().hour(18);
  goToNextWeek = () => {
    this.setState((state) => {
      return {
        firstDay: this.state.firstDay.clone().add(7, "days"),
        startDate: this.state.startDate.clone().add(7, "days"),
        endDate: this.state.endDate.clone().add(7, "days")
      };
    });
  };
  goToPrevWeek = () => {
    this.setState((state) => {
      return {
        firstDay: this.state.firstDay.clone().subtract(7, "days"),
        startDate: this.state.startDate.clone().subtract(7, "days"),
        endDate: this.state.endDate.clone().subtract(7, "days")
      };
    });
  };
  onEventClick = (e) => {
    alert(e);
  };
  
  render() {
    // console.log(WeekCalendar);
    return (
      <div className="App">
        <span>{this.state.startDate.toString()}</span>
        <span>{this.state.endDate.toString()}</span>
        <button onClick={this.goToPrevWeek}>Prev</button>
        <button onClick={this.goToNextWeek}>Next</button>
        <WeekCalendarContainer
          firstDay={this.state.firstDay}
          startTime={this.startTime}
          endTime={this.endTime}
          scaleUnit={60}
          eventSpacing={0}
          selectedIntervals={this.state.selectedIntervals}
          onEventClick={this.onEventClick}
          eventComponent={EventComponent}
          useModal={false}
          cellHeight={80}
        />
      </div>
    );
  }
}

const EventComponent = (props) => (
  <div
    style={{
      backgroundColor: "white",
      height: "100%",
      border: "1px solid blue"
    }}
  >
    asdasd
  </div>
);

EventComponent.propTypes = {
  start: PropTypes.instanceOf(moment).isRequired
};

export default WeekCalendarDep;
