import React from 'react';
import PropTypes from 'prop-types';

const propTypes = {
  startSelection: PropTypes.func.isRequired,
  day:PropTypes.any,
  formHandler:PropTypes.func
};


class DayCell extends React.Component {
  handleMouseDown = (e) => {
    if (e.button === 0) {
      this.props.startSelection();
    }
  }

  render() {
    return (
      <div onMouseDown={this.handleMouseDown} className="dayCell" role="presentation" onClick={()=>this.props.formHandler(this.props.day)}>
          &nbsp;
      </div>);
  }
}

DayCell.propTypes = propTypes;
export default DayCell;
